import React, { useState } from "react"

function App() {
    const [data, setData] = useState({
        date: ""
    })
    const [image,setImage] = useState("https://upload.wikimedia.org/wikipedia/commons/thumb/b/b6/Image_created_with_a_mobile_phone.png/1200px-Image_created_with_a_mobile_phone.png")
    const [information,setInformation] = useState("Lorem ipsum dolor sit amet consectetur adipisicing elit. Fugit, asperiores odio praesentium amet obcaecati nihil iusto, magnam repellat ipsa eos atque exercitationem magni deserunt eius cumque modi a nobis accusamus dolores? Magnam similique asperiores odio sunt inventore. Adipisci, illo eum corporis quae reprehenderit laborum voluptatem voluptate nostrum eligendi voluptatum quia.")

    let name, value
    const handleInputs = (e) => {
        name = e.target.name
        value = e.target.value
        setData({ ...data, [name]: value })
    }
    const postData = async (e) => {
        e.preventDefault()
        const { date } = data
        const res = await fetch("http://localhost:4000/apod-image", {
            method: "POST",
            headers: {
                "Content-type": "application/json"
            },
            body: JSON.stringify({
                date
            })
        })

        const result = await res.json()
        console.log(result)
        if (!result.status) {
            window.alert(result.message)
        } else {
            setImage(result.data.url)
            setInformation(result.data.explanation)
            
        }
        
    }
    return (
            <div>
                <div>
                    <input type="text" name="date" id="date" autoComplete="off" value={data.date} onChange={handleInputs} placeholder="date"></input>
                    <input type="submit" value="Get Image" onClick={postData}></input>
                </div>
                <h3></h3>
                <img src={image} alt="noimage"></img>
                <p>{information}</p>
            </div>

    )
}

export default App
