import React, { useState } from "react"
import "./style.css"

function App() {

    const [data, setData] = useState({
        date: ""
    })

    let img = "https://scontent-bom1-2.xx.fbcdn.net/v/t1.18169-9/13921008_1042525725782888_3286016077468914773_n.png?_nc_cat=108&ccb=1-7&_nc_sid=174925&_nc_ohc=WAPB-8u_XPsAX8kwlcn&_nc_ht=scontent-bom1-2.xx&oh=00_AfCPYXeMkChuw2OWzpRf_O6MwDxLXQ7sV853BU19em2Gzw&oe=63E0FB8D"
    let day = "Discover the cosmos! Each day a different image or photograph of our fascinating universe is featured, along with a brief explanation written by a professional astronomer."
    const [image, setImage] = useState(img)
    const [information, setInformation] = useState(day)

    let name, value
    const handleInputs = (e) => {
        name = e.target.name
        value = e.target.value
        setData({ ...data, [name]: value })
    }
    const postData = async (e) => {
        e.preventDefault()
        const { date } = data
        const res = await fetch("http://localhost:4000/apod-image", {
            method: "POST",
            headers: {
                "Content-type": "application/json"
            },
            body: JSON.stringify({
                date
            })
        })

        const result = await res.json()
        if (!result.status) {
            window.alert(result.message)
        } else {
            setImage(result.data.url)
            setInformation(result.data.explanation)

        }
        // const fetchData = async () => {
        //     const res = await fetch(
        //       `https://api.nasa.gov/planetary/apod?api_key=8G0GNfFlRVtkuMhq3nfzH9bbxwhNLGjGDvmHXO2V&date=${date}`
        //     );
        //     const data = await res.json();
        //     setImage(data.url);
        //     setInformation(data.explanation)
        // };

        // fetchData();
    }

    return (
        <div className="main-card">
            <img src="https://www.nasa.gov/sites/all/themes/custom/nasatwo/images/nasa-logo.svg" width alt="noLogo" />
            <h1>Astronomy Picture of the Day</h1>
            <div className="card1">
                <input type="text" name="date" id="date" autoComplete="off" value={data.date} onChange={handleInputs} placeholder="YYYY-MM-DD"></input>
                <input type="submit" value="Get Image" id="submit" onClick={postData}></input>
            </div>
            <img src={image} alt="noimage"></img>
            <h2>Explanation</h2>
            <p>{information}</p>
        </div>

    )
}

export default App
