import React, { useState } from "react"
import "./style.css"







function App() {
    const [data, setData] = useState({
        date: ""
    })

    const [image, setImage] = useState()
    const [information, setInformation] = useState()


    const fetchData = async () => {
        const res = await fetch(
          `https://api.nasa.gov/planetary/apod?api_key=8G0GNfFlRVtkuMhq3nfzH9bbxwhNLGjGDvmHXO2V&date`
        );
        const data = await res.json();
        
    };
    fetchData();

    let name, value
    const handleInputs = (e) => {
        name = e.target.name
        value = e.target.value
        setData({ ...data, [name]: value })
    }
    const postData = async (e) => {
        e.preventDefault()
        const { date } = data
        const res = await fetch("http://localhost:4000/apod-image", {
            method: "POST",
            headers: {
                "Content-type": "application/json"
            },
            body: JSON.stringify({
                date
            })
        })

        const result = await res.json()
        if (!result.status) {
            window.alert(result.message)
        } else {
            setImage(result.data.url)
            setInformation(result.data.explanation)

        }

    }

    return (
        <div className="main-card">
            <h1>Enter a date to get the Astronomy Picture of the day</h1>
            <div className="card1">
                <input type="text" name="date" id="date" autoComplete="off" value={data.date} onChange={handleInputs} placeholder="YYYY-MM-DD"></input>
                <input type="submit" value="Get Image" id="submit" onClick={postData}></input>
            </div>
            <img src={image} alt="noimage"></img>
            <h3>Explanation</h3>
            <p>{information}</p>
        </div>

    )
}

export default App
